import{Form} from "react-bootstrap";
import React, {useState} from "react";
import { useNavigate} from "react-router-dom";
import "./Login.css";
export default function LOGIN() {
    const [email,setEmail]= useState("")
    const [password,setPassword]= useState("")
   const pic=new URL("./images/oui.jpg",import.meta.url)  
const HandleEmail = (e)=>{
    setEmail( e.target.value)
}
const HandlePassword = (e)=>{
    setPassword(e.target.value)
}
function test(){
    navigate("/InputTodo");
}
    let navigate = useNavigate();
    return (
    
        <div className="d-flex justify-content-center align-items-center">
        <div main className="container__clz">
        <header className="centered_grid__clz">
                      <h1 style={{marginTop:"40px"}}>Login Page</h1>
                  </header>
                  <section className="centered_grid__clz">
                      <div>
                      <div > <label htmlFor="email">EMAIL</label>
                          <input
                              id="email"
                              name="email"
                              placeholder="Insert your email"
                              type="email"
                              onChange={HandleEmail}
                              validate={{
                                  email: {
                                      value: true,
                                      errorMessage:
                                          "Please enter a valid username in the format name@example.com",
                                  },
                                  required: {
                                      value: true,
                                      errorMessage: "This field is required",
                                  },
                              }}
                              style={{marginTop:"25px",marginLeft:"31px"}} 
                          /></div>
                         <div >   <label htmlFor="password">
                              Password
                          </label>
                          <input
                              id="password"
                              name="password"
                              placeholder="Insert a valid password"
                              type="password"
                              validate={{
                                  required: {
                                      value: true,
                                      errorMessage: "This field is required",
                                  },
                              }}
                              onChange={HandlePassword}
                              style={{marginTop:"40px",marginLeft:"12px"}}
                          /></div>
                       
                          <button
                              onClick={()=>
                                  {if( email!=="" && password!=="")
                                  {test()}}}

                                  style={{marginLeft: "96px",
                                    marginTop: "54px"}}
                          >
                              Login
                          </button>
                      </div>
                  </section>
                  </div>
                  </div>
    )
}
