import "./Login.css";
import{Form} from "react-bootstrap";
import React, {useState} from "react";
import { useNavigate} from "react-router-dom";
export default function LOGIN() {
    const [email,setEmail]= useState("")
    const [password,setPassword]= useState("")
   const pic=new URL("./images/oui.jpg",import.meta.url)  
const HandleEmail = (e)=>{
    setEmail( e.target.value)
}
const HandlePassword = (e)=>{
    setPassword(e.target.value)
}
function test(){
    navigate("/InputTodo");
}
    let navigate = useNavigate();
    return (
      <div className="d-flex justify-content-center align-items-center">
      <div main className="container__clz">
      <header className="centered_grid__clz">
                    <h1>Login Page</h1>
                </header>
                <section className="centered_grid__clz">
                    <AvForm onSubmit={test}>
                        <StyledLabel htmlFor="email">EMAIL</StyledLabel>
                        <StyledTextInput
                            id="email"
                            name="email"
                            placeholder="Insert your email"
                            type="email"
                            onChange={HandleEmail}
                            validate={{
                                email: {
                                    value: true,
                                    errorMessage:
                                        "Please enter a valid username in the format name@example.com",
                                },
                                required: {
                                    value: true,
                                    errorMessage: "This field is required",
                                },
                            }}
                        />
                        <StyledLabel htmlFor="password">
                            your password
                        </StyledLabel>
                        <StyledTextInput
                            id="password"
                            name="password"
                            placeholder="Insert a valid password"
                            type="password"
                            validate={{
                                required: {
                                    value: true,
                                    errorMessage: "This field is required",
                                },
                            }}
                            onChange={HandlePassword}
                        />
                        <StyledButton
                            outline={true}
                            rounded={true}
                            block={true}
                            variant="secondary"
                        >
                            Login
                        </StyledButton>
                    </AvForm>
                </section>
